("1","wc-admin-historical-data","update","en_US","WooCommerce Admin: Historical Analytics Data","To view your historical analytics data, you must process your existing orders and customers.","{}","unactioned","woocommerce-admin","2023-02-15 09:18:23","","0","plain","","0","info"),
("2","wc-admin-orders-milestone","info","en_US","First order received","Congratulations on getting your first order! Now is a great time to learn how to manage your orders.","{}","unactioned","woocommerce-admin","2023-02-15 09:18:37","","0","plain","","0","info"),
